  void *sp = ROUND(stack + sizeof(stack) - 4096, 16);
  void *sp_exec = sp;
  int argc = 0;

  // argc
  while (argv[argc]) argc++;
  // 根据手册将 argc 放入栈中
  push(sp, intptr_t, argc);
  // argv[], NULL-terminate
  // 根据手册将 argv 当中所所有的参数传入栈中
  for (int i = 0; i e_entry)); // 跳到程序的入口地址
